mapper.select:
SELECT movie_num movieNum, title, image FROM Movie where 1 = 1
AND title LIKE CONCAT('%', #{searchMovie}, '%')
ORDER BY movie_num LIMIT #{beginRow},#{rowPerPage};

mapper.update:
UPDATE Movie m
JOIN Genre g
ON m.genre = g.genre
SET title = #{title}
    ,m.genre = #{genre}
    ,m.director = #{director}
    ,m.producer = #{producer}
    ,m.release_date = #{releaseDate}
    ,m.runtime = #{runtime}
    ,m.DESCRIPTION = #{description}
    ,m.image = #{image}
    ,m.bcode = #{bcode}
WHERE movie_num = #{movieNum};
