mapper.select:
SELECT movie_num movieNum, title, image FROM Movie where 1 = 1
AND title LIKE CONCAT('%', #{searchMovie}, '%')
ORDER BY movie_num LIMIT #{beginRow},#{rowPerPage};

mapper.update:
UPDATE Movie
SET title = #{title}
    ,Genre = #{genre}
    ,director = #{director}
    ,producer = #{producer}
    ,release_date = #{releaseDate}
    ,runtime = #{runtime}
    ,DESCRIPTION = #{description}
    ,image = #{image}
WHERE movie_num = #{movieNum};
